import { token, refreshAccessToken } from './spotifyAPI';
import Dashboard from './Dashboard';

const spotifyApi = new SpotifyWebApi();

function App() {


  // PLAN, USE THIS AS REFERENCE: https://github.com/aneeshnema/spotify-stats/blob/master/client/src/spotify/index.js
  // BASICALLY create a component for storing all spotify api calls

  // NEXT COMPONENT SHOULD BE REFRESHING TOKEN, IF a certain time has expired we must auto refresh
  // calling back to server and getting new access token
  // CHECK INDEX.html of our auth server for this and ALSO UPDATE OUR ACCESS TOKEN AFTER using setACCES

  console.log("hi")

  // OUR APP SHOULD RETURN 
  // return DASHBOARD OR LOGIN SCREEN
  // our DASHBOARD SHOULD BE ALL THE INFO

  return (
    <div className="App">
      {token ? <Dashboard /> : <Login />}

    </div>
  );
}

