          if (response.is_playing) {
            setIsPlaying(true);
          }
        } else if (!response) {
          setIsPlaying(false);
        }
      })
      .catch(() => {
        getAccessToken()
      });
    }, 7000);
    return () => clearInterval(interval);
  }, [isPlaying]);*/

  //return should return, getTOPTRACKS, make playlist
  // DashBoard should tell us our favourite genre based on our top artist
  return (
    <BrowserRouter>
      <div /* will make a header bar FOR ALL OUR BROWSER ROUTES*/>
        <Navbar />

        <Switch>
          <Route path="/RecentTracks">
            <RecentTracks />
          </Route>
          <Route path="/logout">
            <LogoutButton />
          </Route>
          <Route path="/TopTracks">
            <TopTracks />
          </Route>
          <Route path="/Recommendations">
            <Reccommendations />
          </Route>
          <Route path="/">
            <h1>Welcome to Spoti-FYI</h1>
          </Route>
        </Switch>
        {/*isPlaying ? (
          <div className="player">
            <b>Currently Playing</b>
            <SpotifyPlayer
              token={token}
              syncExternalDevice={true}
              syncExternalDeviceInterval={5}
            />
          </div>
        ) : (
          <div className="player">
            <b>Currently Not Playing Anything </b>
          </div>
        )*/}
      </div>
    </BrowserRouter>
  );
}
