import React, { Component, useState, useEffect } from 'react';
import format from 'date-fns/format';
import { spotifyApi } from '../components/spotifyAPI';
import './Dashboard.css'

const TableItem = (item, index) => (
    <tr key={item.played_at}>
      <td><a href={item.track.uri} class="row-link">{item.track.name}</a></td>
      <td><a href={item.track.uri} tabindex="-1" class="row-link">{item.track.artists.map(e => e.name).join(", ")}</a></td>
      <td><a href={item.track.uri} tabindex="-1" class="row-link">{format(Date.parse(item.played_at), 'dd/MM/yyyy, hh:mma') }</a></td>
    </tr>
);


const RecentTracks = () => {

    const [RecentlyPlayed, setRecentlyPlayed] = useState()

    // variable limit? with useeffect to load more
    const getRecentlyPlayed = () => {

      spotifyApi.getMyRecentlyPlayedTracks({
        limit : 50,
      }).then((response) => {
        if (response) {
          console.log(response)
          setRecentlyPlayed( {
            items: response.items
          });   
        } 
      })

    }
    console.log(RecentlyPlayed)

    return (
        <>
        <div className="recently-played">
        <h2>Recently played tracks</h2>
        <table className="table">
          <thead>
            <tr>
              <th>Track</th>
              <th>Artist(s)</th>
              <th>Played at</th>
            </tr>
          </thead>{getRecentlyPlayed()}
          <tbody>{RecentlyPlayed.items.map((item, index) => TableItem(item, index))}</tbody>
        </table>
        </div>
        </>
    )
    }
export default RecentTracks;
