import React, { useState, useEffect } from "react";
import { spotifyApi } from "../components/spotifyAPI";

import "../App.css";

function Tabs() {
  const [toggleState, setToggleState] = useState(1);

  const toggleTab = (index, timeRange) => {
    changeTerm(timeRange);
    setToggleState(index);
  };

  return (
    <div className="container">
      <div className="bloc-tabs">
        <button
          className={toggleState === 1 ? "tabs active-tabs" : "tabs"}
          onClick={() => toggleTab(1, 'short_term')}
        >
          4 weeks
        </button>
        <button
          className={toggleState === 2 ? "tabs active-tabs" : "tabs"}
          onClick={() => toggleTab(2, 'medium_term')}
        >
          6 months
        </button>
        <button
          className={toggleState === 3 ? "tabs active-tabs" : "tabs"}
          onClick={() => toggleTab(3, 'long_term')}
        >
          All time
        </button>
      </div>

      <div className="content-tabs">
        <div
          className={toggleState === 1 ? "content  active-content" : "content"}
        >
          <h2>Content 1</h2>
          <hr />
          <p>
            Lorem ipsum dolor sit amet consectetur adipisicing elit. Obcaecati
            praesentium incidunt quia aspernatur quasi quidem facilis quo nihil
            vel voluptatum?
          </p>
        </div>

        <div
          className={toggleState === 2 ? "content  active-content" : "content"}
        >
          <h2>Content 2</h2>
          <hr />
          <p>
            Lorem ipsum dolor sit amet consectetur adipisicing elit. Sapiente
            voluptatum qui adipisci.
          </p>
        </div>

        <div
          className={toggleState === 3 ? "content  active-content" : "content"}
        >
          <h2>Content 3</h2>
          <hr />
          <p>
            Lorem ipsum dolor sit amet, consectetur adipisicing elit. Eos sed
            nostrum rerum laudantium totam unde adipisci incidunt modi alias!
            Accusamus in quia odit aspernatur provident et ad vel distinctio
            recusandae totam quidem repudiandae omnis veritatis nostrum
            laboriosam architecto optio rem, dignissimos voluptatum beatae
            aperiam voluptatem atque. Beatae rerum dolores sunt.
          </p>
        </div>
      </div>
    </div>
  );
}

const TopTracks = () => {
  const [tracks, setTracks] = useState();
  const [timeRange, setTimeRange] = useState("short_term");

  const changeTerm = (newTimeRange) => {
    setTimeRange(newTimeRange);
    alert(tracks.items[0].name);
  };

  useEffect(() => {
    spotifyApi
      .getMyTopTracks({
        limit: 50,
        time_range: timeRange,
      })
      .then((response) => {
        if (response) {
          console.log(response);
          setTracks({
            items: response.items,
          });
        }
      });
  }, [timeRange]);

  console.log(tracks);

  return (
    <div>
      {tracks ? (
        <div>
          <Tabs />
          <button onClick={() => changeTerm("short_term")}> 4 Weeks</button>
          <button onClick={() => changeTerm("medium_term")}> 6 months</button>
          <button onClick={() => changeTerm("long_term")}> All Time</button>
        </div>
      ) : (
        <b>LOADING</b>
      )}
    </div>
  );
};

export default TopTracks;
