import React, { Component, useState, useEffect } from 'react';
import SpotifyWebApi from 'spotify-web-api-js';
import { logout } from '../components/functions';
import { spotifyApi } from '../components/spotifyAPI';

export default function Dashboard() {


    // THIS IS the get recentlyplayed component
    const [RecentlyPlayed, setRecentlyPlayed] = useState()


    const TableItem = (item, index) => (
      <tr key={item.played_at}>
        <td>{index + 1}</td>
        <td>{item.track_name}</td>
        <td>{format(item.played_at, 'D MMM YYYY, hh:mma')}</td>
      </tr>
    );

    // variable limit? with useeffect to load more
    const getRecentlyPlayed = () => {



      spotifyApi.getMyTopTracks({
          limit: 20,
          time_range: "short_term",
          offset: 4,
          // offset: shift, next set of entities
      }).then((response) => {
        if (response) {
          console.log(response)
        }
      })

      spotifyApi.getMyRecentlyPlayedTracks({
        limit : 20,
      }).then((response) => {
        if (response) {
          console.log(response)
          setRecentlyPlayed( {

            items: response.items
          });   
          /*
          window.localStorage.setItem('name',response.item.name)
          window.localStorage.setItem('image',response.item.album.images[0].url)     */   
        } 


      })
      return (
      <>
      <div className="recently-played">
      <h2>Recent Tracks</h2>
      <table className="table">
        <thead>
          <tr>
            <th>#</th>
            <th>Song title</th>
            <th>Time</th>
          </tr>
        </thead>
        <tbody>{RecentlyPlayed.items.map((item, index) => TableItem(item, index))}</tbody>
      </table>
      </div>
      </>)
    }

    console.log(RecentlyPlayed)




        //return should return, getTOPTRACKS, make playlist
        return (
            <div className="Dashboard">
                <h1>
                <div>
                    Recently Played:  {  RecentlyPlayed ? RecentlyPlayed.items.map(item => <div><img src={item.track.album.images[0].url} style={{ height: 50 }}/><ul><li>{item.track.name}</li></ul>  </div>) :  <li>loading</li>}
                </div>
                <div>
                    {/*<img src={RecentlyPlayed.albumArt} style={{ height: 150 }}/>*/}
                </div>
                    <button onClick={() => getRecentlyPlayed()}>
                    Check Now Playing
                    </button>
                <button onClick={() => logout()}>
                    Log Out
                </button> 
                </h1>
            </div>
        )
}
