import React, { Component, useState, useEffect } from "react";
import { BrowserRouter, Route, Switch, Link } from "react-router-dom";
import { LogoutButton } from "../components/functions";
import TopTracks from "./TopTracks";
import RecentTracks from "./RecentTracks";
import SpotifyPlayer from "react-spotify-web-playback";
import { spotifyApi } from "../components/spotifyAPI";

export default function Dashboard({ token }) {
  const [isPlaying, setIsPlaying] = useState(false);
  const [lastPlayed, setLastPlayed] = useState();

  useEffect(() => {
    const interval = setInterval( () => {

    spotifyApi.getMyCurrentPlaybackState().then((response) => {
      if (response) {
        if (response.is_playing) {
          setIsPlaying(true);
        }
      }
      console.log(response);
    })
  }, 1000)
    return () => clearInterval(interval)
  }, [isPlaying]);

  useEffect(() => {
    spotifyApi.getMyRecentlyPlayedTracks({ limit: 1 }).then((response) => {
      if (response) {
        setLastPlayed(response.items[0].track.uri);
      }
      console.log(response);
    });
  }, []);
  //return should return, getTOPTRACKS, make playlist
  // DashBoard should tell us our favourite genre based on our top artist
  return (
    <BrowserRouter>
      <div /* will make a header bar FOR ALL OUR BROWSER ROUTES*/>
        <ul>
          <li>
            <Link to="/">HOME</Link>
          </li>
          <li>
            <Link to="/TopTracks">Top Tracks</Link>
          </li>
          <li>
            <Link to="/RecentTracks">RecentlyPlayed</Link>
          </li>
          <li>
            <Link to="/logout">Logout</Link>
          </li>
        </ul>
        {isPlaying ? (
          <SpotifyPlayer
            token={token}
            syncExternalDevice={true}
            syncExternalDeviceInterval={3}
          />
        ) : (
          <b>Not Playing Anything</b>
        )}

        <Switch>
          <Route path="/RecentTracks">
            <RecentTracks />
          </Route>
          <Route path="/logout">
            <LogoutButton />
          </Route>
          <Route path="/TopTracks">
            <TopTracks />
          </Route>
          <Route path="/">
            <TopTracks />
          </Route>
        </Switch>
      </div>
    </BrowserRouter>
  );
}
// ****** NESTED ROUTING
