import React, { Component, useState, useEffect } from 'react';
import SpotifyWebApi from 'spotify-web-api-js';
import { logout } from './functions';

const spotifyApi = new SpotifyWebApi();

export default function Dashboard() {


    // THIS IS the get now playing component
    const [nowPlaying, setNowPlaying] = useState({ name: window.localStorage.getItem('name'), albumArt: window.localStorage.getItem('image')})

    const getNowPlaying = () => {
        spotifyApi.getMyCurrentPlaybackState()
        .then((response) => {
          if (response) {
            setNowPlaying(prevState => ({
              ...prevState,
              name: response.item.name, 
              albumArt: response.item.album.images[0].url
            }));   
            window.localStorage.setItem('name',response.item.name)
            window.localStorage.setItem('image',response.item.album.images[0].url)        
          } else {

            alert("You are not playing anything")
          }

        })
    }
        //return should return, getTOPTRACKS, make playlist
        return (
            <div className="Dashboard">
                <h1>
                <div>
                    Now Playing: { nowPlaying.name }
                </div>
                <div>
                    <img src={nowPlaying.albumArt} style={{ height: 150 }}/>
                </div>
                    <button onClick={() => getNowPlaying()}>
                    Check Now Playing
                    </button>
                <button onClick={() => logout()}>
                    Log Out
                </button> 
                </h1>
            </div>
        )
}
