import React, { Component, useState, useEffect } from 'react';
import { logout } from '../components/functions';
import { spotifyApi } from '../components/spotifyAPI';
import format from 'date-fns/format';
import './Dashboard.css'


const TableItem = (item, index) => (
  <tr key={item.played_at}>
    <td>{item.track.name}</td>
    <td>{item.track.artists.map(e => e.name).join(", ")}</td>
    <td>{format(Date.parse(item.played_at), 'dd/MM/yyyy, hh:mma') }</td>
  </tr>
);


export default function Dashboard() {


    // THIS IS the get recentlyplayed component
    const [RecentlyPlayed, setRecentlyPlayed] = useState()

    /*
    const TableItem = (item, index) => (
      <tr key={item.played_at}>
        <td>{index + 1}</td>
        <td>{item.track.name}</td>
        <td>{format(Date.parse(item.played_at), 'dd/MM/yyyy, hh:mma') }</td>
      </tr>
    );*/



    // variable limit? with useeffect to load more
    const getRecentlyPlayed = () => {

      spotifyApi.getMyRecentlyPlayedTracks({
        limit : 50,
      }).then((response) => {
        if (response) {
          console.log(response)
          setRecentlyPlayed( {
            items: response.items
          });   
        } 
      })

    }
    console.log(RecentlyPlayed)

    const RecentlyPlayedDisplay = () => (
        <>
        <div className="recently-played">
        <h2>Recently played tracks</h2>
        <table className="table">
          <thead>
            <tr>
              <th>Track</th>
              <th>Artist(s)</th>
              <th>Played at</th>
            </tr>
          </thead>
          <tbody>{RecentlyPlayed.items.map((item, index) => TableItem(item, index))}</tbody>
        </table>
        </div>
        </>
    )




  //return should return, getTOPTRACKS, make playlist
  return (
      <div className="Dashboard">

          <button onClick={() => getRecentlyPlayed()}>
          Recently Played
          </button>
          <button onClick={() => logout()}>
              Log Out
          </button> 
          <div>
            {  RecentlyPlayed ? <RecentlyPlayedDisplay/>:  <li>loading</li>
            // CHANGE IT LATER SO THAT IT WILL BE LOADING UNTILL getRECENTLYPLAYED IS LOADED
          }
          </div>
          <div>
              {/*<img src={RecentlyPlayed.albumArt} style={{ height: 150 }}/>*/}
          </div>

      </div>
  )
}
