import React, { useState, useEffect } from "react";
import { spotifyApi } from "../components/spotifyAPI";

const TopTracks = () => {
    const [tracks, setTracks] = useState();
    const [timeRange, setTimeRange] = useState('short_term');

    const changeTerm = (newTimeRange) => {
        setTimeRange(newTimeRange)
        alert(tracks.items[0].name)
    }

    useEffect(() => {
        spotifyApi
        .getMyTopTracks({
            limit:50,
            time_range: timeRange
        })
        .then((response) => {
            if (response) {
              console.log(response);
              setTracks({
                items: response.items,
              });
            }
        });
    }, [timeRange])

    console.log(tracks)

    return (
        <div>
            {tracks? (
            <div>
            <button onClick={()=> changeTerm('short_term')}> 4 Weeks</button>
            <button onClick={()=> changeTerm('medium_term')}> 6 months</button>
            <button onClick={()=> changeTerm('long_term')}> All Time</button>
            </div>

            ): <b>LOADING</b>}


        </div> 
    )
};

export default TopTracks
