            <div className="Dashboard">
                <h1>
                <div>
                    Recently Played: {  RecentlyPlayed ? RecentlyPlayed.items.map(item => <ul><li key={item.track.name}>{item.track.name}</li></ul>) :  <h1>loading</h1>}
                </div>
                <div>
                    {/*<img src={RecentlyPlayed.albumArt} style={{ height: 150 }}/>*/}
                </div>
                    <button onClick={() => getRecentlyPlayed()}>
                    Check Now Playing
                    </button>
                <button onClick={() => logout()}>
                    Log Out
                </button> 
                </h1>
            </div>
        )
}
