import React, { Component, useState, useEffect } from 'react';
import './App.css';
import Login from './Login';
import SpotifyWebApi from 'spotify-web-api-js';

import { token, refreshAccessToken } from './spotifyAPI';

const spotifyApi = new SpotifyWebApi();

function App() {

  const [loggedIn, setLoggedIn] = useState(false)
  const [nowPlaying, setNowPlaying] = useState({ name: window.localStorage.getItem('name'), albumArt: window.localStorage.getItem('image')})

  console.log(window.localStorage.getItem('token_timestamp'))
  console.log(token)

  console.log(window.localStorage.getItem('refresh_token'))
  useEffect(() => {
    if (token) {
      setLoggedIn(true)
      spotifyApi.setAccessToken(token)

    } else {
      setLoggedIn(false)
    }

  },[])

  const refreshToken = () => {
    refreshAccessToken();
  }

  const getNowPlaying = () => {
    spotifyApi.getMyCurrentPlaybackState()
    .then((response) => {
      if (response) {
        setNowPlaying(prevState => ({
          ...prevState,
          name: response.item.name, 
          albumArt: response.item.album.images[0].url
        }));   
        window.localStorage.setItem('name',response.item.name)
        window.localStorage.setItem('image',response.item.album.images[0].url)        
      } else {

        alert("YOU ARE not playing anything")
      }

    })
  }


  // PLAN, USE THIS AS REFERENCE: https://github.com/aneeshnema/spotify-stats/blob/master/client/src/spotify/index.js
  // BASICALLY create a component for storing all spotify api calls

  // NEXT COMPONENT SHOULD BE REFRESHING TOKEN, IF a certain time has expired we must auto refresh
  // calling back to server and getting new access token
  // CHECK INDEX.html of our auth server for this and ALSO UPDATE OUR ACCESS TOKEN AFTER using setACCES



  // OUR APP SHOULD RETURN 
  // return DASHBOARD OR LOGIN SCREEN
  // our DASHBOARD SHOULD BE ALL THE INFO

  return (
    <div className="App">
      {loggedIn ? <h1>
      <div>
        Now Playing/Last Played: { nowPlaying.name }
      </div>
      <div>
        <img src={nowPlaying.albumArt} style={{ height: 150 }}/>
      </div>
      { loggedIn &&
        <button onClick={() => getNowPlaying()}>
          Check Now Playing
        </button>
      }
      <button onClick={() => refreshToken()}>
        refreshAccessToken
      </button> 
      </h1> : 
      <Login />}

    </div>
  )
}

